import { useEffect, useState } from "react";
import axios from "axios";
export const Discussions = () => {
  const [comments, setComments] = useState([]);
  const [gaming, setGaming] = useState([]);
  const [music, setMusic] = useState([]);

  useEffect(() => {
    axios.get("/api/comments").then((res) => {
      console.log("res", res);

      const gamingData = res.data.filter(
        (comment) => comment.topic === "Gaming"
      );
      const musicData = res.data.filter((comment) => comment.topic === "Music");

      setGaming(gamingData);
      setMusic(musicData);
      setComments(res.data);
    });
  }, []);

  const renderComents = (topic) => {
    let result = null;

    let data = null;

    switch (topic) {
      case "Music":
        data = music;
        break;
      case "Gaming":
        data = gaming;
        break;
      default:
        break;
    }

    if (data.length !== 0) {
      result = data.map((comment) => {
        return (
          <div key={comment.id}>
            <p>{comment.name}</p>
            <p>{comment.comment}</p>
            <p>{comment.topic}</p>
            <p>{comment.date}</p>
          </div>
        );
      });
    }

    return result;
  };

  return (
    <>
      <div>{renderComents("Gaming")}</div>
      <h1>Music</h1>
      <div>{renderComents("Music")}</div>
    </>
  );
};
