import { useEffect, useState } from "react";
import axios from "axios";
export const Discussions = () => {
  const [comments, setComments] = useState([]);
  const [gaming, setGaming] = useState([]);
  const [music, setMusic] = useState([]);
  const [userChoice, setUserChoice] = useState("Comments");

  useEffect(() => {
    axios.get("/api/comments").then((res) => {
      console.log("res", res);

      const gamingData = res.data.filter(
        (comment) => comment.topic === "Gaming"
      );
      const musicData = res.data.filter((comment) => comment.topic === "Music");

      setGaming(gamingData);
      setMusic(musicData);
      setComments(res.data);
    });
  }, []);

  const renderComents = (topic) => {
    let result = null;

    let data = null;

    switch (topic) {
      case "Music":
        data = music;
        break;
      case "Gaming":
        data = gaming;
        break;
      default:
        data = comments;
        break;
    }

    if (data.length !== 0) {
      result = data.map((comment) => {
        return (
          <div key={comment.id}>
            <p>{comment.name}</p>
            <p>{comment.comment}</p>
            <p>{comment.topic}</p>
            <p>{comment.date}</p>
          </div>
        );
      });
    }

    return result;
  };

  const handleChoiceSelection = (topic) => {
    setUserChoice(topic);
  };

  return (
    <>
      <div className="btn-group" role="group" aria-label="Basic example">
        <button
          onClick={() => handleChoiceSelection("Gaming")}
          type="button"
          className="btn btn-primary"
        >
          Gaming
        </button>
        <button type="button" className="btn btn-primary">
          Middle
        </button>
        <button type="button" className="btn btn-primary">
          Right
        </button>
      </div>

      <div>{renderComents(userChoice)}</div>
    </>
  );
};
